                    $this->request->data['usagelnkitem']['depreciation_flag'] = $itml[0]['usage_sub_sub_category']['depreciation_flag'];
                    $this->request->data['usagelnkitem']['road_vicinity_flag'] = $itml[0]['usage_sub_sub_category']['road_vicinity_flag'];
