                    } else {

                        $actionvalue = "lblsavemsg";
                    }
                    $this->request->data['minorfunction'] = $this->istrim($this->request->data['minorfunction']);
                    $errarr = $this->validatedata($this->request->data['minorfunction'], $fieldlist);
